  return (
    <Box className='box' sx={{ flexGrow: 1 }}>
      <Grid container spacing={{ xs: 2, md: 3 }} columns={{ xs: 4, sm: 8, md: 12 }}>
        {movies.map((movie)=>{
        return (

          <Grid item xs={2} sm={4} md={3} key={movie.id}>
            <MovieCard movie={movie} on  />
          </Grid>

        )
        })}
      </Grid>
    </Box>
  );
}
export default MovieGrid
