function  MovieForm  ()  {
  const [movieForm,setMovieForm]=useState({
    name:'',
    desc:'',
    imageUrl:'',
    genre:'action'
  })
  return (
    <div className="container">
      <form className="add-toy-form" >
        <h3>Create a Movie</h3>
        <input
          type="text"
          name="name"
          placeholder="Enter a toy's name..."
          className="input-text"
        //   value={toyForm.name}
