import * as React from 'react';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import { Stack,IconButton } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import ThumbUpIcon from '@mui/icons-material/ThumbUp';

 function MovieCard({movie}) {
   const {name,desc,image_url,likes,genre}=movie
  const[words,setWords]=React.useState(true)
  const [likeCount,setLikeCount]=React.useState(likes)
  return (
    <Card sx={{ maxWidth: 345 }}>
      <CardMedia
        component="img"
        alt="green iguana"
        height="300"
        image={image_url}
      />
      <CardContent>
        <Typography className='name' gutterBottom variant="h5" component="div">
          {name.toUpperCase()}

        </Typography>
        <p className='genre' >{genre.name}</p>
        <p className='desc'>
          {words?`${desc.slice(0,30)}...`:desc}<button className='btn' onClick={()=>{setWords(!words)}}>{words?'Read more':'Read Less'}</button>
        </p>
        <h4>spa{likeCount} likes</h4>
      </CardContent>
      <CardActions>
        <Stack  spacing={2} direction="row"  >
        <IconButton   onClick={()=>{setLikeCount((like)=>like+1)}} ><ThumbUpIcon/></IconButton>
        <IconButton><DeleteIcon/></IconButton>
        </Stack>
      </CardActions>
    </Card>
  );
}
export default MovieCard
